*(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|\[([^\[\]\r\\]|\\.)*\](?:(?:\r\n)?[ \t])*)(?:\.(?:(?:\r\n)?[ \t])*(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|\[([^\[\]\r\\]|\\.)*\](?:(?:\r\n)?[ \t])*))*\>(?:(?:\r\n)?[ \t])*)(?:,\s*(?:(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|"(?:[^\"\r\\]|\\.|(?:(?:\r\n)?[ \t]))*"(?:(?:\r\n)?[ \t])*)(?:\.(?:(?:\r\n)?[ \t])*(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|"(?:[^\"\r\\]|\\.|(?:(?:\r\n)?[ \t]))*"(?:(?:\r\n)?[ \t])*))*@(?:(?:\r\n)?[ \t])*(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|\[([^\[\]\r\\]|\\.)*\](?:(?:\r\n)?[ \t])*)(?:\.(?:(?:\r\n)?[ \t])*(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|\[([^\[\]\r\\]|\\.)*\](?:(?:\r\n)?[ \t])*))*|(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|"(?:[^\"\r\\]|\\.|(?:(?:\r\n)?[ \t]))*"(?:(?:\r\n)?[ \t])*)*\<(?:(?:\r\n)?[ \t])*(?:@(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|\[([^\[\]\r\\]|\\.)*\](?:(?:\r\n)?[ \t])*)(?:\.(?:(?:\r\n)?[ \t])*(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|\[([^\[\]\r\\]|\\.)*\](?:(?:\r\n)?[ \t])*))*(?:,@(?:(?:\r\n)?[\t])*(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|\[([^\[\]\r\\]|\\.)*\](?:(?:\r\n)?[ \t])*)(?:\.(?:(?:\r\n)?[ \t])*(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|\[([^\[\]\r\\]|\\.)*\](?:(?:\r\n)?[ \t])*))*)*:(?:(?:\r\n)?[ \t])*)?(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|"(?:[^\"\r\\]|\\.|(?:(?:\r\n)?[ \t]))*"(?:(?:\r\n)?[ \t])*)(?:\.(?:(?:\r\n)?[ \t])*(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|"(?:[^\"\r\\]|\\.|(?:(?:\r\n)?[ \t]))*"(?:(?:\r\n)?[ \t])*))*@(?:(?:\r\n)?[ \t])*(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|\[([^\[\]\r\\]|\\.)*\](?:(?:\r\n)?[ \t])*)(?:\.(?:(?:\r\n)?[ \t])*(?:[^()<>@,;:\\".\[\] \000-\031]+(?:(?:(?:\r\n)?[ \t])+|\Z|(?=[\["()<>@,;:\\".\[\]]))|\[([^\[\]\r\\]|\\.)*\](?:(?:\r\n)?[ \t])*))*\>(?:(?:\r\n)?[ \t])*))*)?;\s*)$/

  PRE_SPLIT_EMAIL_MISTAKES = {
      'gmail,com' => 'gmail.com',
      /@@+/ => '@',
      /@ +/ => '@',
      / +@/ => '@',
      /([.])\1+/i => ".",
      /([.])\1+$/i => "",
      /@co.in$/ => '.co.in',
      '.@' => '@',
      '@.' => '@',
  }

  POST_SPLIT_EMAIL_MISTAKES = {
      /^[\.\[\]]+/ => '',
      /[\.\[\]]+$/ => '',
      /yahoo.co$/ => 'yahoo.com',
      /yahoocom$/ => 'yahoo.com',
      /yahoo\.coin$/ => 'yahoo.co.in',
      /yahooco\.in$/ => 'yahoo.co.in',
      /[|*^{}&%`!#'"()<>?]/ => '', # general cleanup
  }

  INCOMLETE_TYPING_MISTAKES = {
    /@yahoo.co((m){1}|(.([a-z]{2})){1})$/ => {:incorrect_regex => /@yaho([.a-z]{0,8})$/, :correct_string => '@yahoo.com'},
    /@gmail.com$/ => {:incorrect_regex => /@gma([@.a-z]{0,10})$/, :correct_string => '@gmail.com'},
    /@rediff(mail)?.com$/ => {:incorrect_regex => /@rediff([.a-z]{0,9})$/, :correct_string => '@rediffmail.com'},
    /@hotmail.com$/ => {:incorrect_regex => /@hotm([.a-z]{0,8})$/, :correct_string => '@hotmail.com'},
    /@outlook.com$/ => {:incorrect_regex => /@outlook([.a-z]{0,5})$/, :correct_string => '@outlook.com'}
  }

  MULTI_FIELD_SEPARATOR = /[:;, \/\n]/

  def format_emails(emails)
    formatted_emails = []
    emails.to_a.each do |value|
      value = Encoder.only_ascii(value.downcase)
      PRE_SPLIT_EMAIL_MISTAKES.each do |mistaken, intended|
        value = value.gsub(mistaken, intended)
      end

      value.split(MULTI_FIELD_SEPARATOR).each do |email|
        email = email.strip
        next if email.size < 7 # x@gm.in -> shortest possible email

        POST_SPLIT_EMAIL_MISTAKES.each do |mistaken, intended|
          email = email.gsub(mistaken, intended)
        end

        email = email.clean_up_typoed_email

        INCOMLETE_TYPING_MISTAKES.each do |correct_regex, email_mistake|
          unless email =~ correct_regex
            email = email.gsub(email_mistake[:incorrect_regex], email_mistake[:correct_string])
          end
        end

        # ignoring invalid emails
        formatted_emails << email if email.match(::EmailFormatUtility::CUSTOM_EMAIL_REGEX)
      end
    end
    return formatted_emails.uniq.compact
  end

end